import React, { useState } from 'react';
import '../assets/AdminPanel.css';

const AdminPanel = ({ 
  products, 
  categories, 
  users, 
  onAddProduct, 
  onUpdateProduct, 
  onDeleteProduct, 
  onAddCategory, 
  onDeleteCategory,
  onDeleteUser  // Добавил пропс для удаления пользователя
}) => {
  const [activeTab, setActiveTab] = useState('products');
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    description: '',
    categoryId: '',
    images: []
  });
  const [newCategory, setNewCategory] = useState({
    name: '',
    description: ''
  });
  const [newProductFiles, setNewProductFiles] = useState([]); // для файлов
  const [editingProductFiles, setEditingProductFiles] = useState([]); // для файлов при редактировании

  // Добавление товара с отправкой FormData (вызов пропса onAddProduct должен принимать FormData)
  const handleAddProduct = async (e) => {
    e.preventDefault();

    const category = categories.find(c => c.id === parseInt(newProduct.categoryId));
    if (!category) {
      alert('Пожалуйста, выберите категорию!');
      return;
    }

    if (!newProduct.name || !newProduct.price || !newProduct.description || !newProduct.categoryId) {
      alert('Заполните все поля!');
      return;
    }

    const formData = new FormData();
    formData.append('name', newProduct.name);
    formData.append('price', newProduct.price);
    formData.append('description', newProduct.description);
    formData.append('categoryId', newProduct.categoryId);
    formData.append('categoryName', category.name);
    newProductFiles.forEach(file => formData.append('images', file));

    const success = await onAddProduct(formData);
    if (success) {
      setNewProduct({ name: '', price: '', description: '', categoryId: '', images: [] });
      setNewProductFiles([]);
      alert('Товар успешно добавлен!');
    }
  };

  // Обновление товара с FormData
  const handleUpdateProduct = async (e) => {
    e.preventDefault();

    const category = categories.find(c => c.id === parseInt(editingProduct.categoryId));
    if (!category) {
      alert('Пожалуйста, выберите категорию!');
      return;
    }

    if (!editingProduct.name || !editingProduct.price || !editingProduct.description || !editingProduct.categoryId) {
      alert('Заполните все поля!');
      return;
    }

    const formData = new FormData();
    formData.append('name', editingProduct.name);
    formData.append('price', editingProduct.price);
    formData.append('description', editingProduct.description);
    formData.append('categoryId', editingProduct.categoryId);
    formData.append('categoryName', category.name);
    editingProductFiles.forEach(file => formData.append('images', file));

    await onUpdateProduct(editingProduct.id, formData);
    setEditingProduct(null);
    setEditingProductFiles([]);
    alert('Товар успешно обновлен!');
  };

  // Для нового товара — добавляем выбранные файлы к уже существующим, но не больше 5 всего
  const handleNewProductFilesChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setNewProductFiles(prevFiles => {
      const combinedFiles = [...prevFiles, ...selectedFiles].slice(0, 5);
      return combinedFiles;
    });
  };

  // Аналогично для редактирования товара
  const handleEditingProductFilesChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setEditingProductFiles(prevFiles => {
      const combinedFiles = [...prevFiles, ...selectedFiles].slice(0, 5);
      return combinedFiles;
    });
  };

  // Удалить выбранное фото из нового товара (по индексу)
  const removeNewProductFile = (index) => {
    setNewProductFiles(prev => prev.filter((_, i) => i !== index));
  };

  // Удалить выбранное фото из редактируемого товара (по индексу)
  const removeEditingProductFile = (index) => {
    setEditingProductFiles(prev => prev.filter((_, i) => i !== index));
  };

  // Добавление категории (оставляем как есть)
  const handleAddCategory = (e) => {
    e.preventDefault();
    if (onAddCategory(newCategory)) {
      setNewCategory({ name: '', description: '' });
      alert('Категория успешно добавлена!');
    }
  };

  return (
    <div className="admin-container">
      <h1>Админ панель</h1>
      
      <div className="admin-tabs">
        <button 
          className={`admin-tab ${activeTab === 'products' ? 'active' : ''}`}
          onClick={() => setActiveTab('products')}
        >
          Товары
        </button>
        <button 
          className={`admin-tab ${activeTab === 'categories' ? 'active' : ''}`}
          onClick={() => setActiveTab('categories')}
        >
          Категории
        </button>
        <button 
          className={`admin-tab ${activeTab === 'users' ? 'active' : ''}`}
          onClick={() => setActiveTab('users')}
        >
          Пользователи
        </button>
      </div>

      {/* Управление товарами */}
      <div className={`admin-section ${activeTab === 'products' ? 'active' : ''}`}>
        <h2>Управление товарами</h2>
        
        {/* Добавление товара */}
        <div className="admin-card">
          <h3>Добавить товар</h3>
          <form onSubmit={handleAddProduct} className="admin-form">
            <input
              type="text"
              placeholder="Название товара"
              value={newProduct.name}
              onChange={(e) => setNewProduct(prev => ({ ...prev, name: e.target.value }))}
              required
            />
            <input
              type="number"
              step="0.01"
              placeholder="Цена"
              value={newProduct.price}
              onChange={(e) => setNewProduct(prev => ({ ...prev, price: e.target.value }))}
              required
            />
            <select
              value={newProduct.categoryId}
              onChange={(e) => setNewProduct(prev => ({ ...prev, categoryId: e.target.value }))}
              required
            >
              <option value="">Выберите категорию</option>
              {categories.map(category => (
                <option key={category.id} value={category.id}>{category.name}</option>
              ))}
            </select>
            <textarea
              placeholder="Описание товара"
              value={newProduct.description}
              onChange={(e) => setNewProduct(prev => ({ ...prev, description: e.target.value }))}
              required
            />
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleNewProductFilesChange}
            />
            {/* Превью выбранных файлов для нового товара */}
            {newProductFiles.length > 0 && (
              <div className="product-images-preview" style={{display: 'flex', gap: '10px', marginTop: '10px', flexWrap: 'wrap'}}>
                {newProductFiles.map((file, idx) => (
                  <div key={idx} style={{position: 'relative'}}>
                    <img
                      src={URL.createObjectURL(file)}
                      alt={`preview-${idx}`}
                      width={80}
                      style={{borderRadius: '8px', objectFit: 'cover'}}
                    />
                    <button
                      type="button"
                      onClick={() => removeNewProductFile(idx)}
                      style={{
                        position: 'absolute',
                        top: '-8px',
                        right: '-8px',
                        background: 'red',
                        color: 'white',
                        borderRadius: '50%',
                        border: 'none',
                        width: '20px',
                        height: '20px',
                        cursor: 'pointer',
                        fontWeight: 'bold',
                        lineHeight: '18px',
                        padding: 0,
                      }}
                      title="Удалить фото"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>
            )}
            <button type="submit" className="admin-btn">Добавить товар</button>
          </form>
        </div>

        {/* Список товаров */}
        <div className="admin-grid">
          {products.length === 0 ? (
            <div className="admin-card"><p>Товары пока не добавлены</p></div>
          ) : (
            products.map(product => (
              <div key={product.id} className="admin-card">
                <h4>{product.name}</h4>
                <p>Цена: {product.price} ₽</p>
                <p>Категория: {product.categoryName}</p>
                <p>{product.description.substring(0, 100)}...</p>
                {product.images && product.images.length > 0 && (
                  <div className="product-images">
                    {product.images.map((img, idx) => (
                      <img key={idx} src={`http://localhost:5000${img}`} alt={product.name} width={80} />
                    ))}
                  </div>
                )}
                <div className="admin-actions">
                  <button onClick={() => {
                    setEditingProduct(product);
                    setEditingProductFiles([]); // очищаем файлы при открытии редактирования
                  }} className="admin-btn edit-btn">✏️</button>
                  <button onClick={() => { onDeleteProduct(product.id); alert('Товар удален!'); }} className="admin-btn delete-btn">Удалить</button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Управление категориями */}
      <div className={`admin-section ${activeTab === 'categories' ? 'active' : ''}`}>
        <h2>Управление категориями</h2>
        
        <div className="admin-card">
          <h3>Добавить категорию</h3>
          <form onSubmit={handleAddCategory} className="admin-form">
            <input
              type="text"
              placeholder="Название категории"
              value={newCategory.name}
              onChange={(e) => setNewCategory(prev => ({ ...prev, name: e.target.value }))}
              required
            />
            <textarea
              placeholder="Описание категории"
              value={newCategory.description}
              onChange={(e) => setNewCategory(prev => ({ ...prev, description: e.target.value }))}
              required
            />
            <button type="submit" className="admin-btn">Добавить категорию</button>
          </form>
        </div>

        <div className="admin-grid">
          {categories.map(category => (
            <div key={category.id} className="admin-card">
              <h4>{category.name}</h4>
              <p>{category.description}</p>
              <button onClick={() => { onDeleteCategory(category.id); alert('Категория удалена!'); }} className="admin-btn delete-btn">Удалить категорию</button>
            </div>
          ))}
        </div>
      </div>

      {/* Пользователи */}
      <div className={`admin-section ${activeTab === 'users' ? 'active' : ''}`}>
        <h2>Зарегистрированные пользователи</h2>
        <div className="admin-grid">
          {users.length === 0 ? (
            <div className="admin-card"><p>Пользователи пока не зарегистрированы</p></div>
          ) : (
            users.map((user, idx) => (
              <div key={idx} className="admin-card">
                <h4>Пользователь {idx + 1}</h4>
                <p><b>Имя:</b> {user.name || 'Без имени'}</p>
                <p><b>Email:</b> {user.email}</p>
                <p><b>Время входа:</b> {new Date(user.loginTime).toLocaleString()}</p>
                <button
                  className="admin-btn delete-btn"
                  onClick={() => {
                    if(window.confirm(`Удалить пользователя ${user.email}?`)) {
                      onDeleteUser(user.id);
                      alert('Пользователь удален!');
                    }
                  }}
                >
                  Удалить пользователя
                </button>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Модальное окно редактирования товара */}
      {editingProduct && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Редактировать товар</h3>
            <form onSubmit={handleUpdateProduct} className="admin-form">
              <input
                type="text"
                placeholder="Название товара"
                value={editingProduct.name}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, name: e.target.value }))}
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Цена"
                value={editingProduct.price}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, price: e.target.value }))}
                required
              />
              <select
                value={editingProduct.categoryId}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, categoryId: e.target.value }))}
                required
              >
                {categories.map(category => (
                  <option key={category.id} value={category.id}>{category.name}</option>
                ))}
              </select>
              <textarea
                placeholder="Описание товара"
                value={editingProduct.description}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, description: e.target.value }))}
                required
              />
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handleEditingProductFilesChange}
              />
              {/* Превью выбранных файлов при редактировании */}
              {editingProductFiles.length > 0 && (
                <div className="product-images-preview" style={{display: 'flex', gap: '10px', marginTop: '10px', flexWrap: 'wrap'}}>
                  {editingProductFiles.map((file, idx) => (
                    <div key={idx} style={{position: 'relative'}}>
                      <img
                        src={URL.createObjectURL(file)}
                        alt={`edit-preview-${idx}`}
                        width={80}
                        style={{borderRadius: '8px', objectFit: 'cover'}}
                      />
                      <button
                        type="button"
                        onClick={() => removeEditingProductFile(idx)}
                        style={{
                          position: 'absolute',
                          top: '-8px',
                          right: '-8px',
                          background: 'red',
                          color: 'white',
                          borderRadius: '50%',
                          border: 'none',
                          width: '20px',
                          height: '20px',
                          cursor: 'pointer',
                          fontWeight: 'bold',
                          lineHeight: '18px',
                          padding: 0,
                        }}
                        title="Удалить фото"
                      >
                        ×
                      </button>
                    </div>
                  ))}
                </div>
              )}
              <div className="modal-actions">
                <button type="submit" className="admin-btn">Сохранить</button>
                <button type="button" onClick={() => setEditingProduct(null)} className="admin-btn delete-btn">Отмена</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
