import React, { useState, useMemo } from 'react';
import { useParams, Link } from 'react-router-dom';

const Category = ({ categories, products }) => {
  const { id } = useParams();
  const [searchQuery, setSearchQuery] = useState('');

  const category = categories.find(c => c.id === parseInt(id));
  const categoryProducts = products.filter(p => p.categoryId === parseInt(id));

  // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è —Ç–æ–≤–∞—Ä–æ–≤ –ø–æ –ø–æ–∏—Å–∫–æ–≤–æ–º—É –∑–∞–ø—Ä–æ—Å—É
  const filteredProducts = useMemo(() => {
    if (!searchQuery.trim()) {
      return categoryProducts;
    }

    const query = searchQuery.toLowerCase().trim();
    return categoryProducts.filter(product => 
      product.name.toLowerCase().includes(query) ||
      product.description.toLowerCase().includes(query)
    );
  }, [categoryProducts, searchQuery]);

  if (!category) {
    return <div>–ö–∞—Ç–µ–≥–æ—Ä–∏—è –Ω–µ –Ω–∞–π–¥–µ–Ω–∞</div>;
  }

  return (
    <div className="category-page">
      <div className="category-header">
        <h1>{category.name}</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder={`–ü–æ–∏—Å–∫ –≤ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ "${category.name}"...`}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="category-search"
          />
        </div>
        <p>–ù–∞–π–¥–µ–Ω–æ —Ç–æ–≤–∞—Ä–æ–≤: {filteredProducts.length} –∏–∑ {categoryProducts.length}</p>
      </div>

      {categoryProducts.length === 0 ? (
        <div className="no-products">
          <p>–í –¥–∞–Ω–Ω–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –ø–æ–∫–∞ –Ω–µ—Ç —Ç–æ–≤–∞—Ä–æ–≤</p>
        </div>
      ) : filteredProducts.length === 0 ? (
        <div className="no-products">
          <p>–ü–æ –∑–∞–ø—Ä–æ—Å—É "{searchQuery}" –Ω–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ</p>
          <p>–ü–æ–ø—Ä–æ–±—É–π—Ç–µ –∏–∑–º–µ–Ω–∏—Ç—å –ø–æ–∏—Å–∫–æ–≤—ã–π –∑–∞–ø—Ä–æ—Å</p>
        </div>
      ) : (
        <div className="products-grid">
          {filteredProducts.map(product => (
            <Link 
              key={product.id} 
              to={`/product/${product.id}`} 
              className="product-card"
            >
              <div className="product-image">
                {product.images && product.images.length > 0 ? (
                  <img 
                    src={`http://localhost:5000${product.images[0]}`} 
                    alt={product.name} 
                  />
                ) : (
                  'üì¶'
                )}
              </div>
              <div className="product-info">
                <h3 className="product-name">{product.name}</h3>
                <div className="product-price">{product.price} ‚Çº</div>
                <p className="product-description">
                  {product.description.substring(0, 100)}...
                </p>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Category;
