import React, { useState } from 'react';
import '../assets/AdminPanel.css';

const AdminPanel = ({ 
  products, 
  categories, 
  users, 
  onAddProduct, 
  onUpdateProduct, 
  onDeleteProduct, 
  onAddCategory, 
  onDeleteCategory 
}) => {
  const [activeTab, setActiveTab] = useState('products');
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    description: '',
    categoryId: '',
    images: []
  });
  const [newCategory, setNewCategory] = useState({
    name: '',
    description: ''
  });

  const handleAddProduct = (e) => {
    e.preventDefault();
    console.log('Form submitted with:', newProduct); // Для отладки
    
    const category = categories.find(c => c.id === parseInt(newProduct.categoryId));
    if (!category) {
      alert('Пожалуйста, выберите категорию!');
      return;
    }

    const productData = {
      ...newProduct,
      price: parseFloat(newProduct.price),
      categoryId: parseInt(newProduct.categoryId),
      categoryName: category.name
    };
    
    console.log('Sending product data:', productData); // Для отладки
    
    if (onAddProduct(productData)) {
      setNewProduct({
        name: '',
        price: '',
        description: '',
        categoryId: '',
        images: []
      });
      alert('Товар успешно добавлен!');
    }
  };

  const handleUpdateProduct = (e) => {
    e.preventDefault();
    const category = categories.find(c => c.id === parseInt(editingProduct.categoryId));
    const productData = {
      ...editingProduct,
      price: parseFloat(editingProduct.price),
      categoryId: parseInt(editingProduct.categoryId),
      categoryName: category.name
    };
    
    onUpdateProduct(editingProduct.id, productData);
    setEditingProduct(null);
    alert('Товар успешно обновлен!');
  };

  const handleAddCategory = (e) => {
    e.preventDefault();
    console.log('Adding category:', newCategory); // Для отладки
    
    if (onAddCategory(newCategory)) {
      setNewCategory({ name: '', description: '' });
      alert('Категория успешно добавлена!');
    }
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const imageUrls = files.map(file => URL.createObjectURL(file));
    
    if (editingProduct) {
      setEditingProduct(prev => ({
        ...prev,
        images: [...(prev.images || []), ...imageUrls].slice(0, 5)
      }));
    } else {
      setNewProduct(prev => ({
        ...prev,
        images: [...(prev.images || []), ...imageUrls].slice(0, 5)
      }));
    }
  };

  return (
    <div className="admin-container">
      <h1>Админ панель</h1>
      
      <div className="admin-tabs">
        <button 
          className={`admin-tab ${activeTab === 'products' ? 'active' : ''}`}
          onClick={() => setActiveTab('products')}
        >
          Товары
        </button>
        <button 
          className={`admin-tab ${activeTab === 'categories' ? 'active' : ''}`}
          onClick={() => setActiveTab('categories')}
        >
          Категории
        </button>
        <button 
          className={`admin-tab ${activeTab === 'users' ? 'active' : ''}`}
          onClick={() => setActiveTab('users')}
        >
          Пользователи
        </button>
      </div>

      {/* Products Section */}
      <div className={`admin-section ${activeTab === 'products' ? 'active' : ''}`}>
        <h2>Управление товарами</h2>
        
        {/* Add Product Form */}
        <div className="admin-card">
          <h3>Добавить товар</h3>
          <form onSubmit={handleAddProduct} className="admin-form">
            <input
              type="text"
              placeholder="Название товара"
              value={newProduct.name}
              onChange={(e) => setNewProduct(prev => ({ ...prev, name: e.target.value }))}
              required
            />
            <input
              type="number"
              step="0.01"
              placeholder="Цена"
              value={newProduct.price}
              onChange={(e) => setNewProduct(prev => ({ ...prev, price: e.target.value }))}
              required
            />
            <select
              value={newProduct.categoryId}
              onChange={(e) => setNewProduct(prev => ({ ...prev, categoryId: e.target.value }))}
              required
            >
              <option value="">Выберите категорию</option>
              {categories.map(category => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
            <textarea
              placeholder="Описание товара"
              value={newProduct.description}
              onChange={(e) => setNewProduct(prev => ({ ...prev, description: e.target.value }))}
              required
            />
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleImageChange}
            />
            <button type="submit" className="admin-btn">Добавить товар</button>
          </form>
        </div>

        {/* Products List */}
        <div className="admin-grid">
          {products.length === 0 ? (
            <div className="admin-card">
              <p>Товары пока не добавлены</p>
            </div>
          ) : (
            products.map(product => (
              <div key={product.id} className="admin-card">
                <h4>{product.name}</h4>
                <p>Цена: {product.price} ₽</p>
                <p>Категория: {product.categoryName}</p>
                <p>{product.description.substring(0, 100)}...</p>
                
                <div className="admin-actions">
                  <button 
                    onClick={() => setEditingProduct(product)}
                    className="admin-btn edit-btn"
                  >
                    Редактировать
                  </button>
                  <button 
                    onClick={() => {
                      onDeleteProduct(product.id);
                      alert('Товар удален!');
                    }}
                    className="admin-btn delete-btn"
                  >
                    Удалить
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Categories Section */}
      <div className={`admin-section ${activeTab === 'categories' ? 'active' : ''}`}>
        <h2>Управление категориями</h2>
        
        {/* Add Category Form */}
        <div className="admin-card">
          <h3>Добавить категорию</h3>
          <form onSubmit={handleAddCategory} className="admin-form">
            <input
              type="text"
              placeholder="Название категории"
              value={newCategory.name}
              onChange={(e) => setNewCategory(prev => ({ ...prev, name: e.target.value }))}
              required
            />
            <textarea
              placeholder="Описание категории"
              value={newCategory.description}
              onChange={(e) => setNewCategory(prev => ({ ...prev, description: e.target.value }))}
              required
            />
            <button type="submit" className="admin-btn">Добавить категорию</button>
          </form>
        </div>

        {/* Categories List */}
        <div className="admin-grid">
          {categories.map(category => (
            <div key={category.id} className="admin-card">
              <h4>{category.name}</h4>
              <p>{category.description}</p>
              <button 
                onClick={() => {
                  onDeleteCategory(category.id);
                  alert('Категория удалена!');
                }}
                className="admin-btn delete-btn"
              >
                Удалить категорию
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Users Section */}
      <div className={`admin-section ${activeTab === 'users' ? 'active' : ''}`}>
        <h2>Зарегистрированные пользователи</h2>
        <div className="admin-grid">
          {users.length === 0 ? (
            <div className="admin-card">
              <p>Пользователи пока не зарегистрированы</p>
            </div>
          ) : (
            users.map((user, index) => (
              <div key={index} className="admin-card">
                <h4>Пользователь {index + 1}</h4>
                <p>Email: {user.email}</p>
                <p>Телефон: {user.phone}</p>
                <p>Время входа: {new Date(user.loginTime).toLocaleString()}</p>
              </div>
            ))
          )}
        </div>
      </div>

      {/* Edit Product Modal */}
      {editingProduct && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Редактировать товар</h3>
            <form onSubmit={handleUpdateProduct} className="admin-form">
              <input
                type="text"
                placeholder="Название товара"
                value={editingProduct.name}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, name: e.target.value }))}
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="Цена"
                value={editingProduct.price}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, price: e.target.value }))}
                required
              />
              <select
                value={editingProduct.categoryId}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, categoryId: e.target.value }))}
                required
              >
                {categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
              <textarea
                placeholder="Описание товара"
                value={editingProduct.description}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, description: e.target.value }))}
                required
              />
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handleImageChange}
              />
              <div className="modal-actions">
                <button type="submit" className="admin-btn">Сохранить</button>
                <button 
                  type="button" 
                  onClick={() => setEditingProduct(null)}
                  className="admin-btn delete-btn"
                >
                  Отмена
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
