import React, { useState } from 'react';
import '../assets/AdminPanel.css';

const AdminPanel = ({ 
  products, 
  categories, 
  users, 
  onAddProduct, 
  onUpdateProduct, 
  onDeleteProduct, 
  onAddCategory, 
  onDeleteCategory 
}) => {
  const [activeTab, setActiveTab] = useState('products');
  const [editingProduct, setEditingProduct] = useState(null);
  const [newProduct, setNewProduct] = useState({
    name: '',
    price: '',
    description: '',
    categoryId: '',
    images: []
  });
  const [newCategory, setNewCategory] = useState({
    name: '',
    description: ''
  });
  const [newProductFiles, setNewProductFiles] = useState([]); // –¥–ª—è —Ñ–∞–π–ª–æ–≤
  const [editingProductFiles, setEditingProductFiles] = useState([]); // –¥–ª—è —Ñ–∞–π–ª–æ–≤ –ø—Ä–∏ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏

  // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞ —Å –æ—Ç–ø—Ä–∞–≤–∫–æ–π FormData (–≤—ã–∑–æ–≤ –ø—Ä–æ–ø—Å–∞ onAddProduct –¥–æ–ª–∂–µ–Ω –ø—Ä–∏–Ω–∏–º–∞—Ç—å FormData)
  const handleAddProduct = async (e) => {
    e.preventDefault();

    const category = categories.find(c => c.id === parseInt(newProduct.categoryId));
    if (!category) {
      alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é!');
      return;
    }

    if (!newProduct.name || !newProduct.price || !newProduct.description || !newProduct.categoryId) {
      alert('–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è!');
      return;
    }

    const formData = new FormData();
    formData.append('name', newProduct.name);
    formData.append('price', newProduct.price);
    formData.append('description', newProduct.description);
    formData.append('categoryId', newProduct.categoryId);
    formData.append('categoryName', category.name);
    newProductFiles.forEach(file => formData.append('images', file));

    const success = await onAddProduct(formData);
    if (success) {
      setNewProduct({ name: '', price: '', description: '', categoryId: '', images: [] });
      setNewProductFiles([]);
      alert('–¢–æ–≤–∞—Ä —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω!');
    }
  };

  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞ —Å FormData
  const handleUpdateProduct = async (e) => {
    e.preventDefault();

    const category = categories.find(c => c.id === parseInt(editingProduct.categoryId));
    if (!category) {
      alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é!');
      return;
    }

    if (!editingProduct.name || !editingProduct.price || !editingProduct.description || !editingProduct.categoryId) {
      alert('–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –ø–æ–ª—è!');
      return;
    }

    const formData = new FormData();
    formData.append('name', editingProduct.name);
    formData.append('price', editingProduct.price);
    formData.append('description', editingProduct.description);
    formData.append('categoryId', editingProduct.categoryId);
    formData.append('categoryName', category.name);
    editingProductFiles.forEach(file => formData.append('images', file));

    await onUpdateProduct(editingProduct.id, formData);
    setEditingProduct(null);
    setEditingProductFiles([]);
    alert('–¢–æ–≤–∞—Ä —É—Å–ø–µ—à–Ω–æ –æ–±–Ω–æ–≤–ª–µ–Ω!');
  };

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã–±–æ—Ä–∞ —Ñ–∞–π–ª–æ–≤ –¥–ª—è –Ω–æ–≤–æ–≥–æ —Ç–æ–≤–∞—Ä–∞
  const handleNewProductFilesChange = (e) => {
    const files = Array.from(e.target.files).slice(0, 5);
    setNewProductFiles(files);
  };

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã–±–æ—Ä–∞ —Ñ–∞–π–ª–æ–≤ –ø—Ä–∏ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–∏
  const handleEditingProductFilesChange = (e) => {
    const files = Array.from(e.target.files).slice(0, 5);
    setEditingProductFiles(files);
  };

  // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ (–æ—Å—Ç–∞–≤–ª—è–µ–º –∫–∞–∫ –µ—Å—Ç—å)
  const handleAddCategory = (e) => {
    e.preventDefault();
    if (onAddCategory(newCategory)) {
      setNewCategory({ name: '', description: '' });
      alert('–ö–∞—Ç–µ–≥–æ—Ä–∏—è —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω–∞!');
    }
  };

  return (
    <div className="admin-container">
      <h1>–ê–¥–º–∏–Ω –ø–∞–Ω–µ–ª—å</h1>
      
      <div className="admin-tabs">
        <button 
          className={`admin-tab ${activeTab === 'products' ? 'active' : ''}`}
          onClick={() => setActiveTab('products')}
        >
          –¢–æ–≤–∞—Ä—ã
        </button>
        <button 
          className={`admin-tab ${activeTab === 'categories' ? 'active' : ''}`}
          onClick={() => setActiveTab('categories')}
        >
          –ö–∞—Ç–µ–≥–æ—Ä–∏–∏
        </button>
        <button 
          className={`admin-tab ${activeTab === 'users' ? 'active' : ''}`}
          onClick={() => setActiveTab('users')}
        >
          –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏
        </button>
      </div>

      {/* –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞–º–∏ */}
      <div className={`admin-section ${activeTab === 'products' ? 'active' : ''}`}>
        <h2>–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞–º–∏</h2>
        
        {/* –î–æ–±–∞–≤–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞ */}
        <div className="admin-card">
          <h3>–î–æ–±–∞–≤–∏—Ç—å —Ç–æ–≤–∞—Ä</h3>
          <form onSubmit={handleAddProduct} className="admin-form">
            <input
              type="text"
              placeholder="–ù–∞–∑–≤–∞–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞"
              value={newProduct.name}
              onChange={(e) => setNewProduct(prev => ({ ...prev, name: e.target.value }))}
              required
            />
            <input
              type="number"
              step="0.01"
              placeholder="–¶–µ–Ω–∞"
              value={newProduct.price}
              onChange={(e) => setNewProduct(prev => ({ ...prev, price: e.target.value }))}
              required
            />
            <select
              value={newProduct.categoryId}
              onChange={(e) => setNewProduct(prev => ({ ...prev, categoryId: e.target.value }))}
              required
            >
              <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—é</option>
              {categories.map(category => (
                <option key={category.id} value={category.id}>{category.name}</option>
              ))}
            </select>
            <textarea
              placeholder="–û–ø–∏—Å–∞–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞"
              value={newProduct.description}
              onChange={(e) => setNewProduct(prev => ({ ...prev, description: e.target.value }))}
              required
            />
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleNewProductFilesChange}
            />
            <button type="submit" className="admin-btn">–î–æ–±–∞–≤–∏—Ç—å —Ç–æ–≤–∞—Ä</button>
          </form>
        </div>

        {/* –°–ø–∏—Å–æ–∫ —Ç–æ–≤–∞—Ä–æ–≤ –≤ —Ç–∞–±–ª–∏—Ü–µ */}
        <div className="admin-table-wrapper">
          {products.length === 0 ? (
            <div className="admin-card"><p>–¢–æ–≤–∞—Ä—ã –ø–æ–∫–∞ –Ω–µ –¥–æ–±–∞–≤–ª–µ–Ω—ã</p></div>
          ) : (
            <table className="admin-table">
              <thead>
                <tr>
                  <th>–ù–∞–∑–≤–∞–Ω–∏–µ</th>
                  <th>–¶–µ–Ω–∞ (‚ÇΩ)</th>
                  <th>–ö–∞—Ç–µ–≥–æ—Ä–∏—è</th>
                  <th>–û–ø–∏—Å–∞–Ω–∏–µ</th>
                  <th>–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è</th>
                  <th>–î–µ–π—Å—Ç–≤–∏—è</th>
                </tr>
              </thead>
              <tbody>
                {products.map(product => (
                  <tr key={product.id}>
                    <td>{product.name}</td>
                    <td>{product.price}</td>
                    <td>{product.categoryName}</td>
                    <td>{product.description.length > 100 ? product.description.substring(0, 100) + '...' : product.description}</td>
                    <td>
                      {product.images && product.images.length > 0 ? (
                        product.images.map((img, idx) => (
                          <img
                            key={idx}
                            src={`http://localhost:5000${img}`}
                            alt={product.name}
                            width={50}
                            style={{ marginRight: '5px', borderRadius: '4px' }}
                          />
                        ))
                      ) : (
                        <span>–ù–µ—Ç –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π</span>
                      )}
                    </td>
                    <td>
                      <button onClick={() => setEditingProduct(product)} className="admin-btn edit-btn" title="–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å">‚úèÔ∏è</button>
                      <button
                        onClick={() => { 
                          if(window.confirm('–£–¥–∞–ª–∏—Ç—å —Ç–æ–≤–∞—Ä?')) {
                            onDeleteProduct(product.id);
                            alert('–¢–æ–≤–∞—Ä —É–¥–∞–ª–µ–Ω!');
                          }
                        }}
                        className="admin-btn delete-btn"
                        title="–£–¥–∞–ª–∏—Ç—å"
                      >
                        üóëÔ∏è
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>

      {/* –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º–∏ */}
      <div className={`admin-section ${activeTab === 'categories' ? 'active' : ''}`}>
        <h2>–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º–∏</h2>
        
        <div className="admin-card">
          <h3>–î–æ–±–∞–≤–∏—Ç—å –∫–∞—Ç–µ–≥–æ—Ä–∏—é</h3>
          <form onSubmit={handleAddCategory} className="admin-form">
            <input
              type="text"
              placeholder="–ù–∞–∑–≤–∞–Ω–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏"
              value={newCategory.name}
              onChange={(e) => setNewCategory(prev => ({ ...prev, name: e.target.value }))}
              required
            />
            <textarea
              placeholder="–û–ø–∏—Å–∞–Ω–∏–µ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏"
              value={newCategory.description}
              onChange={(e) => setNewCategory(prev => ({ ...prev, description: e.target.value }))}
              required
            />
            <button type="submit" className="admin-btn">–î–æ–±–∞–≤–∏—Ç—å –∫–∞—Ç–µ–≥–æ—Ä–∏—é</button>
          </form>
        </div>

        <div className="admin-grid">
          {categories.map(category => (
            <div key={category.id} className="admin-card">
              <h4>{category.name}</h4>
              <p>{category.description}</p>
              <button onClick={() => { if(window.confirm('–£–¥–∞–ª–∏—Ç—å –∫–∞—Ç–µ–≥–æ—Ä–∏—é?')) { onDeleteCategory(category.id); alert('–ö–∞—Ç–µ–≥–æ—Ä–∏—è —É–¥–∞–ª–µ–Ω–∞!'); }}} className="admin-btn delete-btn">–£–¥–∞–ª–∏—Ç—å –∫–∞—Ç–µ–≥–æ—Ä–∏—é</button>
            </div>
          ))}
        </div>
      </div>

      {/* –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ */}
      <div className={`admin-section ${activeTab === 'users' ? 'active' : ''}`}>
        <h2>–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏</h2>
        <div className="admin-grid">
          {users.length === 0 ? (
            <div className="admin-card"><p>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–∏ –ø–æ–∫–∞ –Ω–µ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω—ã</p></div>
          ) : (
            users.map((user, idx) => (
              <div key={idx} className="admin-card">
                <h4>–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å {idx + 1}</h4>
                <p>Email: {user.email}</p>
                <p>–í—Ä–µ–º—è –≤—Ö–æ–¥–∞: {new Date(user.loginTime).toLocaleString()}</p>
              </div>
            ))
          )}
        </div>
      </div>

      {/* –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ —Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏—è —Ç–æ–≤–∞—Ä–∞ */}
      {editingProduct && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å —Ç–æ–≤–∞—Ä</h3>
            <form onSubmit={handleUpdateProduct} className="admin-form">
              <input
                type="text"
                placeholder="–ù–∞–∑–≤–∞–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞"
                value={editingProduct.name}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, name: e.target.value }))}
                required
              />
              <input
                type="number"
                step="0.01"
                placeholder="–¶–µ–Ω–∞"
                value={editingProduct.price}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, price: e.target.value }))}
                required
              />
              <select
                value={editingProduct.categoryId}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, categoryId: e.target.value }))}
                required
              >
                {categories.map(category => (
                  <option key={category.id} value={category.id}>{category.name}</option>
                ))}
              </select>
              <textarea
                placeholder="–û–ø–∏—Å–∞–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞"
                value={editingProduct.description}
                onChange={(e) => setEditingProduct(prev => ({ ...prev, description: e.target.value }))}
                required
              />
              <input
                type="file"
                multiple
                accept="image/*"
                onChange={handleEditingProductFilesChange}
              />
              <div className="modal-actions">
                <button type="submit" className="admin-btn">–°–æ—Ö—Ä–∞–Ω–∏—Ç—å</button>
                <button type="button" onClick={() => setEditingProduct(null)} className="admin-btn delete-btn">–û—Ç–º–µ–Ω–∞</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
