import React from 'react';
import { useParams, Link } from 'react-router-dom';

const ProductDetail = ({ products }) => {
  const { id } = useParams();
  const product = products.find(p => p.id === parseInt(id));

  if (!product) {
    return <div>–¢–æ–≤–∞—Ä –Ω–µ –Ω–∞–π–¥–µ–Ω</div>;
  }

  return (
    <div className="product-detail">
      <div className="product-detail-container">
        <div className="product-images">
          {product.images && product.images.length > 0 ? (
            <div className="image-gallery">
              {product.images.map((image, index) => (
                <img 
                  key={index} 
                  src={image} 
                  alt={`${product.name} ${index + 1}`}
                  className="product-image"
                />
              ))}
            </div>
          ) : (
            <div className="product-image-placeholder">
              üì¶
            </div>
          )}
        </div>
        
        <div className="product-info">
          <h1 className="product-title">{product.name}</h1>
          <div className="product-price">{product.price} ‚ÇΩ</div>
          <div className="product-category">
            –ö–∞—Ç–µ–≥–æ—Ä–∏—è: {product.categoryName}
          </div>
          <div className="product-description">
            <h3>–û–ø–∏—Å–∞–Ω–∏–µ:</h3>
            <p>{product.description}</p>
          </div>
          
          <div className="product-actions">
            <button className="buy-btn">–ö—É–ø–∏—Ç—å</button>
            <Link to={`/category/${product.categoryId}`} className="back-btn">
              –ù–∞–∑–∞–¥ –∫ –∫–∞—Ç–µ–≥–æ—Ä–∏–∏
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
