import React from 'react';
import { useParams, Link } from 'react-router-dom';
import './Category.css';

const Category = ({ categories, products }) => {
  const { id } = useParams();
  const category = categories.find(c => c.id === parseInt(id));
  const categoryProducts = products.filter(p => p.categoryId === parseInt(id));

  if (!category) {
    return <div>–ö–∞—Ç–µ–≥–æ—Ä–∏—è –Ω–µ –Ω–∞–π–¥–µ–Ω–∞</div>;
  }

  return (
    <div className="category-page">
      <div className="category-header">
        <h1>{category.name}</h1>
        <p>–ù–∞–π–¥–µ–Ω–æ —Ç–æ–≤–∞—Ä–æ–≤: {categoryProducts.length}</p>
      </div>
      
      {categoryProducts.length === 0 ? (
        <div className="no-products">
          <p>–í –¥–∞–Ω–Ω–æ–π –∫–∞—Ç–µ–≥–æ—Ä–∏–∏ –ø–æ–∫–∞ –Ω–µ—Ç —Ç–æ–≤–∞—Ä–æ–≤</p>
        </div>
      ) : (
        <div className="products-grid">
          {categoryProducts.map(product => (
            <Link 
              key={product.id} 
              to={`/product/${product.id}`} 
              className="product-card"
            >
              <div className="product-image">
                {product.images && product.images.length > 0 ? (
                  <img src={product.images[0]} alt={product.name} />
                ) : (
                  'üì¶'
                )}
              </div>
              <div className="product-info">
                <h3 className="product-name">{product.name}</h3>
                <div className="product-price">{product.price} ‚ÇΩ</div>
                <p className="product-description">
                  {product.description.substring(0, 100)}...
                </p>
              </div>
            </Link>
          ))}
        </div>
      )}
    </div>
  );
};

export default Category;
