import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../assets/ProductDetail.css';

const ProductDetail = ({ products }) => {
  const { id } = useParams();
  const product = products.find(p => p.id === parseInt(id));
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  if (!product) return <div>Товар не найден</div>;

  const openModal = (index) => {
    setCurrentImageIndex(index);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const showPrevImage = () => {
    setCurrentImageIndex(prev => (prev === 0 ? product.images.length - 1 : prev - 1));
  };

  const showNextImage = () => {
    setCurrentImageIndex(prev => (prev === product.images.length - 1 ? 0 : prev + 1));
  };

  return (
    <div className="product-detail-container">
      <div className="product-card" onClick={() => openModal(0)}>
        <div className="product-image-container">
          <img
            src={product.images[0]}
            alt={product.name}
            className="product-main-image"
          />
          {product.images.length > 1 && (
            <div className="image-counter">+{product.images.length - 1}</div>
          )}
        </div>
        <div className="product-info">
          <h2 className="product-title">{product.name}</h2>
          <div className="product-price">{product.price} AZN</div>
        </div>
      </div>

      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <span className="close-modal" onClick={closeModal}>&times;</span>
            
            <div className="modal-product-container">
              {/* Блок с фотографиями */}
              <div className="modal-product-images">
                <div className="main-image-container">
                  <img
                    src={product.images[currentImageIndex]}
                    alt={product.name}
                    className="modal-main-image"
                  />
                  <button className="modal-arrow left" onClick={showPrevImage}>&lsaquo;</button>
                  <button className="modal-arrow right" onClick={showNextImage}>&rsaquo;</button>
                </div>
                <div className="thumbnails-container">
                  {product.images.map((img, index) => (
                    <img
                      key={index}
                      src={img}
                      alt={`Thumbnail ${index + 1}`}
                      className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                      onClick={() => setCurrentImageIndex(index)}
                    />
                  ))}
                </div>
              </div>
              
              {/* Блок с информацией */}
              <div className="modal-product-info">
                <h2 className="modal-product-title">{product.name}</h2>
                <div className="modal-product-price">{product.price} AZN</div>
                
                {product.oldPrice && (
                  <div className="old-price">Старая цена: {product.oldPrice} AZN</div>
                )}
                
                <div className="modal-product-description">
                  <h3>Описание:</h3>
                  <p>{product.description}</p>
                </div>
                
                <div className="product-specs">
                  <div className="spec-row">
                    <span className="spec-label">Категория:</span>
                    <span className="spec-value">{product.category}</span>
                  </div>
                  <div className="spec-row">
                    <span className="spec-label">Материал:</span>
                    <span className="spec-value">{product.material}</span>
                  </div>
                  <div className="spec-row">
                    <span className="spec-label">В наличии:</span>
                    <span className="spec-value">{product.inStock ? 'Да' : 'Нет'}</span>
                  </div>
                </div>
                
                <button className="buy-button">Добавить в корзину</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDetail;
