import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { User, Mail, LogIn } from 'lucide-react';
import '../assets/Login.css'; // твой CSS из первого варианта

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateForm = () => {
    const newErrors = {};

    if (!username) {
      newErrors.username = 'Имя обязательно';
    } else if (!/^[A-Za-zА-Яа-яЁё]{3,}$/.test(username)) {
      newErrors.username = 'Имя должно содержать только буквы и быть не менее 3 символов';
    }

    if (!email) {
      newErrors.email = 'Email обязателен';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      newErrors.email = 'Введите корректный email';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      onLogin(email, username);
      navigate('/');
    }
  };

  return (
    <div className="login-container">
      <h2>Вход в систему</h2>
      <form onSubmit={handleSubmit} className="login-form">
        <div className="form-group">
          <label htmlFor="username" style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
            <User size={20} />
            Имя:
          </label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => {
              const val = e.target.value.replace(/[^A-Za-zА-Яа-яЁё]/g, '');
              setUsername(val);
            }}
            placeholder="Введите имя (только буквы, минимум 3)"
            className={errors.username ? 'error' : ''}
            required
          />
          {errors.username && <span className="error-message" style={{ color: 'red', fontSize: '0.85rem' }}>{errors.username}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="email" style={{ display: 'flex', alignItems: 'center', gap: '6px' }}>
            <Mail size={20} />
            Email:
          </label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Введите ваш email"
            className={errors.email ? 'error' : ''}
            required
          />
          {errors.email && <span className="error-message" style={{ color: 'red', fontSize: '0.85rem' }}>{errors.email}</span>}
        </div>

        <button type="submit" className="login-btn" style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
          <LogIn size={20} />
          Войти
        </button>
      </form>
    </div>
  );
};

export default Login;
