import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import '../assets/App.css';
import Header from './Header';
import Home from './Home';
import Category from './Category';
import Login from './Login';
import AdminPanel from './AdminPanel';
import ProductDetail from './ProductDetail';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [userData, setUserData] = useState(null);
  const [categories, setCategories] = useState([
    { id: 1, name: 'Авто запчасти', image: '/images/auto.jpg' },
    { id: 2, name: 'Стулья', image: '/images/chairs.jpg' },
    { id: 3, name: 'Столы', image: '/images/tables.jpg' },
    { id: 4, name: 'Диваны', image: '/images/sofas.jpg' },
    { id: 5, name: 'Вазы', image: '/images/vases.jpg' }
  ]);
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const savedProducts = localStorage.getItem('products');
    const savedUsers = localStorage.getItem('users');
    const savedCategories = localStorage.getItem('categories');
    
    if (savedProducts) setProducts(JSON.parse(savedProducts));
    if (savedUsers) setUsers(JSON.parse(savedUsers));
    if (savedCategories) setCategories(JSON.parse(savedCategories));
  }, []);

  useEffect(() => {
    localStorage.setItem('products', JSON.stringify(products));
  }, [products]);

  useEffect(() => {
    localStorage.setItem('users', JSON.stringify(users));
  }, [users]);

  useEffect(() => {
    localStorage.setItem('categories', JSON.stringify(categories));
  }, [categories]);

  const handleLogin = (email, phone) => {
    const newUser = { email, phone, loginTime: new Date().toISOString() };
    setUsers(prev => [...prev, newUser]);
    
    if (email === 'fastandtrust1223' && phone === '200722') {
      setIsAdmin(true);
      setIsLoggedIn(true);
      setUserData(newUser);
    } else {
      setIsLoggedIn(true);
      setUserData(newUser);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setIsAdmin(false);
    setUserData(null);
  };

  const addProduct = (product) => {
    console.log('Adding product:', product); // Для отладки
    
    // Проверяем, что все поля заполнены
    if (!product.name || !product.price || !product.description || !product.categoryId) {
      alert('Пожалуйста, заполните все поля!');
      return false;
    }

    const categoryProducts = products.filter(p => p.categoryId === product.categoryId);
    if (categoryProducts.length >= 100) {
      alert('Максимум 100 товаров в категории!');
      return false;
    }

    // Создаем новый товар с уникальным ID
    const newProduct = {
      ...product,
      id: Date.now(),
      price: parseFloat(product.price),
      categoryId: parseInt(product.categoryId)
    };

    // Добавляем товар в список
    setProducts(prev => [...prev, newProduct]);
    
    console.log('Product added successfully:', newProduct); // Для отладки
    return true;
  };

  const updateProduct = (id, updatedProduct) => {
    console.log('Updating product:', id, updatedProduct); // Для отладки
    
    setProducts(prev => prev.map(p => {
      if (p.id === id) {
        return {
          ...p,
          ...updatedProduct,
          price: parseFloat(updatedProduct.price),
          categoryId: parseInt(updatedProduct.categoryId)
        };
      }
      return p;
    }));
  };

  const deleteProduct = (id) => {
    console.log('Deleting product:', id); // Для отладки
    setProducts(prev => prev.filter(p => p.id !== id));
  };

  const addCategory = (category) => {
    console.log('Adding category:', category); // Для отладки
    
    if (!category.name || !category.description) {
      alert('Пожалуйста, заполните все поля!');
      return false;
    }

    const newCategory = {
      ...category,
      id: Date.now()
    };

    setCategories(prev => [...prev, newCategory]);
    console.log('Category added successfully:', newCategory); // Для отладки
    return true;
  };

  const deleteCategory = (id) => {
    console.log('Deleting category:', id); // Для отладки
    setCategories(prev => prev.filter(c => c.id !== id));
    setProducts(prev => prev.filter(p => p.categoryId !== id));
  };

  return (
    <Router>
      <div className="App">
        <Header 
          isLoggedIn={isLoggedIn} 
          isAdmin={isAdmin} 
          onLogout={handleLogout}
          userData={userData}
        />
        <main className="main-content">
          <Routes>
            <Route path="/" element={<Home categories={categories} />} />
            <Route path="/category/:id" element={<Category categories={categories} products={products} />} />
            <Route path="/product/:id" element={<ProductDetail products={products} />} />
            <Route path="/login" element={
              !isLoggedIn ? <Login onLogin={handleLogin} /> : <Navigate to="/" />
            } />
            <Route path="/admin" element={
              isAdmin ? (
                <AdminPanel 
                  products={products}
                  categories={categories}
                  users={users}
                  onAddProduct={addProduct}
                  onUpdateProduct={updateProduct}
                  onDeleteProduct={deleteProduct}
                  onAddCategory={addCategory}
                  onDeleteCategory={deleteCategory}
                />
              ) : <Navigate to="/login" />
            } />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
