import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../assets/ProductDetail.css';

const ProductDetail = ({ products }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const product = products.find(p => p.id === parseInt(id));
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isZoomed, setIsZoomed] = useState(false);
  const [isTextExpanded, setIsTextExpanded] = useState(false);

  if (!product) return <div className="not-found">Товар не найден</div>;

  const showPrevImage = () => {
    setIsZoomed(false);
    setCurrentImageIndex(prev => (prev === 0 ? product.images.length - 1 : prev - 1));
  };

  const showNextImage = () => {
    setIsZoomed(false);
    setCurrentImageIndex(prev => (prev === product.images.length - 1 ? 0 : prev + 1));
  };

  const toggleZoom = () => setIsZoomed(!isZoomed);
  const toggleText = () => setIsTextExpanded(!isTextExpanded);

  // Автоматическое определение, нужно ли обрезать текст
  const shouldTruncate = product.description.length > 300;
  const displayedText = shouldTruncate && !isTextExpanded
    ? `${product.description.slice(0, 300)}...`
    : product.description;

  return (
    <div className="product-modal-overlay">
      <div className="product-modal">
        {/* Левая часть с фото */}
        <div className="product-modal-left">
          <img
            src={product.images[currentImageIndex]}
            alt={product.name}
            className={`product-modal-image ${isZoomed ? 'zoomed' : ''}`}
            onClick={toggleZoom}
          />
          <button className="modal-arrow left" onClick={showPrevImage}>&lsaquo;</button>
          <button className="modal-arrow right" onClick={showNextImage}>&rsaquo;</button>
          
          <div className="product-thumbnails">
            {product.images.map((img, index) => (
              <img
                key={index}
                src={img}
                alt={`Thumbnail ${index + 1}`}
                className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => {
                  setCurrentImageIndex(index);
                  setIsZoomed(false);
                }}
              />
            ))}
          </div>
        </div>
        
        {/* Правая часть с информацией */}
        <div className="product-modal-right">
          <h1 className="product-modal-title">{product.name}</h1>
          <div className="product-modal-price">{product.price} AZN</div>
          
          <div className="user-content">
            <div className="description-text">
              {displayedText}
            </div>
            {shouldTruncate && (
              <button onClick={toggleText} className="text-toggle-btn">
                {isTextExpanded ? 'Свернуть' : 'Читать далее'}
              </button>
            )}
          </div>
          
          <div className="modal-actions">
            <button onClick={() => navigate(-1)} className="back-btn">Назад</button>
            <button className="add-to-cart-btn">Добавить в корзину</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
