import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import '../assets/ProductDetail.css';

const ProductDetail = ({ products }) => {
  const { id } = useParams();
  const product = products.find(p => p.id === parseInt(id));
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  if (!product) return <div>Товар не найден</div>;

  const openModal = (index) => {
    setCurrentImageIndex(index);
    setIsModalOpen(true);
  };

  const closeModal = (e) => {
    if (
      e.target.classList.contains('modal-overlay') ||
      e.target.classList.contains('close-modal')
    ) {
      setIsModalOpen(false);
    }
  };

  const showPrevImage = (e) => {
    e.stopPropagation();
    setCurrentImageIndex(prev => (prev === 0 ? product.images.length - 1 : prev - 1));
  };

  const showNextImage = (e) => {
    e.stopPropagation();
    setCurrentImageIndex(prev => (prev === product.images.length - 1 ? 0 : prev + 1));
  };

  return (
    <div className="product-detail-container">
      <div className="product-card">
        <div className="product-image-container" onClick={() => openModal(0)}>
          <img
            src={product.images[0]}
            alt={product.name}
            className="product-main-image"
          />
          {product.images.length > 1 && (
            <div className="image-counter">+{product.images.length - 1}</div>
          )}
        </div>

        <div className="product-info">
          <h1 className="product-title">{product.name}</h1>
          <div className="product-description">
            <p>{product.description}</p>
          </div>
          <div className="product-price">{product.price} AZN</div>
          <div className="product-actions">
            <button className="buy-btn">Sifariş et</button>
            <Link to={`/category/${product.categoryId}`} className="back-btn">
              Geri
            </Link>
          </div>
        </div>
      </div>

      {isModalOpen && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal-content">
            <span className="close-modal" onClick={closeModal}>&times;</span>
            <button className="modal-arrow left" onClick={showPrevImage}>&lsaquo;</button>
            <img
              src={product.images[currentImageIndex]}
              alt={product.name}
              className="modal-image"
              onClick={(e) => e.stopPropagation()}
            />
            <button className="modal-arrow right" onClick={showNextImage}>&rsaquo;</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDetail;
