import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import '../assets/App.css';
import Header from './Header';
import Home from './Home';
import Category from './Category';
import Login from './Login';
import AdminPanel from './AdminPanel';
import ProductDetail from './ProductDetail';
import CartPage from './CartPage'; // Компонент корзины
import { CartProvider } from './CartContent'; // Провайдер корзины

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [userData, setUserData] = useState(null);

  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);

  // Загрузка данных с сервера при старте
  useEffect(() => {
    const fetchData = async () => {
      try {
        const [productsRes, categoriesRes, usersRes] = await Promise.all([
          fetch('http://localhost:5000/products'),
          fetch('http://localhost:5000/categories'),
          fetch('http://localhost:5000/users'),
        ]);

        if (!productsRes.ok || !categoriesRes.ok || !usersRes.ok) {
          throw new Error('Ошибка загрузки данных с сервера');
        }

        const productsData = await productsRes.json();
        const categoriesData = await categoriesRes.json();
        const usersData = await usersRes.json();

        setProducts(productsData);
        setCategories(categoriesData);
        setUsers(usersData);
      } catch (error) {
        console.error(error);
        alert('Ошибка при загрузке данных с сервера');
      }
    };

    fetchData();
  }, []);

  // Логин пользователя
  const handleLogin = (email, username) => {
    const newUser = { email, username, loginTime: new Date().toISOString() };
    setUsers(prev => [...prev, newUser]);

    if (
      email.toLowerCase() === 'fastandtrust@1223.com' &&
      username.toLowerCase() === 'fastandtrust'
    ) {
      setIsAdmin(true);
      setIsLoggedIn(true);
      setUserData(newUser);
    } else {
      setIsLoggedIn(true);
      setUserData(newUser);
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setIsAdmin(false);
    setUserData(null);
  };

  // Добавление товара (принимает FormData с файлами)
  const addProduct = async (formData) => {
    try {
      const res = await fetch('http://localhost:5000/products', {
        method: 'POST',
        // ! ВАЖНО: НЕ указываем 'Content-Type' для FormData, браузер сам его выставит
        body: formData,
      });

      if (!res.ok) throw new Error('Ошибка при добавлении товара');

      const newProduct = await res.json();
      setProducts(prev => [...prev, newProduct]);
      return true;
    } catch (error) {
      alert(error.message);
      return false;
    }
  };

  // Обновление товара (также с FormData)
  const updateProduct = async (id, formData) => {
    try {
      const res = await fetch(`http://localhost:5000/products/${id}`, {
        method: 'PUT',
        // 'Content-Type' не указываем, т.к. это FormData
        body: formData,
      });

      if (!res.ok) throw new Error('Ошибка при обновлении товара');

      const updated = await res.json();

      setProducts(prev => prev.map(p => (p.id === id ? updated : p)));
      return true;
    } catch (error) {
      alert(error.message);
      return false;
    }
  };

  // Удаление товара
  const deleteProduct = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/products/${id}`, {
        method: 'DELETE',
      });

      if (!res.ok) throw new Error('Ошибка при удалении товара');

      setProducts(prev => prev.filter(p => p.id !== id));
    } catch (error) {
      alert(error.message);
    }
  };

  // Добавление категории
  const addCategory = async (category) => {
    if (!category.name || !category.description) {
      alert('Пожалуйста, заполните все поля!');
      return false;
    }

    try {
      const res = await fetch('http://localhost:5000/categories', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(category),
      });

      if (!res.ok) throw new Error('Ошибка при добавлении категории');

      const newCategory = await res.json();
      setCategories(prev => [...prev, newCategory]);
      return true;
    } catch (error) {
      alert(error.message);
      return false;
    }
  };

  // Удаление категории (с удалением товаров этой категории)
  const deleteCategory = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/categories/${id}`, {
        method: 'DELETE',
      });

      if (!res.ok) throw new Error('Ошибка при удалении категории');

      setCategories(prev => prev.filter(c => c.id !== id));
      setProducts(prev => prev.filter(p => p.categoryId !== id));
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <CartProvider>
      <Router>
        <div className="App">
          <Header 
            isLoggedIn={isLoggedIn} 
            isAdmin={isAdmin} 
            onLogout={handleLogout}
            userData={userData}
          />
          <main className="main-content">
            <Routes>
              <Route path="/" element={<Home categories={categories} />} />
              <Route path="/category/:id" element={<Category categories={categories} products={products} />} />
              <Route path="/product/:id" element={<ProductDetail products={products} />} />
              <Route path="/cart" element={<CartPage />} />
              <Route path="/login" element={
                !isLoggedIn ? (
                  <Login onLogin={handleLogin} />
                ) : isAdmin ? (
                  <Navigate to="/admin" />
                ) : (
                  <Navigate to="/" />
                )
              } />
              <Route path="/admin" element={
                isAdmin ? (
                  <AdminPanel 
                    products={products}
                    categories={categories}
                    users={users}
                    onAddProduct={addProduct}
                    onUpdateProduct={updateProduct}
                    onDeleteProduct={deleteProduct}
                    onAddCategory={addCategory}
                    onDeleteCategory={deleteCategory}
                  />
                ) : (
                  <Navigate to="/login" />
                )
              } />
            </Routes>
          </main>
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
