import React, { useState, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { CartContext } from './CartContent';
import '../assets/ProductDetail.css';

export const ProductCard = ({ product }) => {
  const navigate = useNavigate();

  return (
    <div className="product-card" onClick={() => navigate(`/product/${product.id}`)}>
      <div className="product-image-container">
        <img
          src={`http://localhost:5000${product.images[0]}`}
          alt={product.name}
          className="product-image"
        />
      </div>
      <div className="product-info">
        <h3 className="product-title">{product.name}</h3>
        <div className="product-price">{product.price} ₽</div>
        <div className="product-description">
          {product.description.length > 100
            ? `${product.description.substring(0, 100)}...`
            : product.description}
        </div>
      </div>
    </div>
  );
};

const ProductDetail = ({ products }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useContext(CartContext);
  const product = products.find(p => p.id === parseInt(id));
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [isZoomed, setIsZoomed] = useState(false);

  if (!product) return <div className="not-found">Товар не найден</div>;

  const showPrevImage = () => {
    setIsZoomed(false);
    setCurrentImageIndex(prev =>
      prev === 0 ? product.images.length - 1 : prev - 1
    );
  };

  const showNextImage = () => {
    setIsZoomed(false);
    setCurrentImageIndex(prev =>
      prev === product.images.length - 1 ? 0 : prev + 1
    );
  };

  const toggleZoom = () => setIsZoomed(!isZoomed);

  const handleAddToCart = () => {
    addToCart(product);
    navigate('/cart');
  };

  return (
    <div className="product-modal-overlay">
      <div className="product-modal">
        <div className="product-modal-left">
          <img
            src={`http://localhost:5000${product.images[currentImageIndex]}`}
            alt={product.name}
            className={`product-modal-image ${isZoomed ? 'zoomed' : ''}`}
            onClick={toggleZoom}
          />
          <button className="modal-arrow left" onClick={showPrevImage}>
            &lsaquo;
          </button>
          <button className="modal-arrow right" onClick={showNextImage}>
            &rsaquo;
          </button>

          <div className="product-thumbnails">
            {product.images.map((img, index) => (
              <img
                key={index}
                src={`http://localhost:5000${img}`}
                alt={`Thumbnail ${index + 1}`}
                className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => {
                  setCurrentImageIndex(index);
                  setIsZoomed(false);
                }}
              />
            ))}
          </div>
        </div>

        <div className="product-modal-right">
          <h1 className="product-modal-title">{product.name}</h1>
          <div className="product-modal-price">{product.price} AZN</div>

          <div className="description-container">
            <div className="description-text">{product.description}</div>
          </div>

          <div className="modal-actions">
            <button onClick={() => navigate(-1)} className="back-btn">
              Назад
            </button>
            <button onClick={handleAddToCart} className="add-to-cart-btn">
              Добавить в корзину
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
